import amqp, { Channel, ChannelModel, ConsumeMessage } from 'amqplib';
import SETTINGS from '../config/settings';

type Handler<T> = (payload: T) => Promise<void>;

const SERVICE_NAME = '[profiles-service]';

export class MessageBroker {
  private connection?: ChannelModel;
  private channel?: Channel;

  private async getConnection(): Promise<ChannelModel> {
    if (this.connection) {
      return this.connection;
    }
    const connection = await amqp.connect(SETTINGS.MESSAGE_BROKER_URL);
    connection.on('error', (err: Error) => {
      console.error(`${SERVICE_NAME} RabbitMQ connection error`, err);
    });
    connection.on('close', () => {
      console.warn(`${SERVICE_NAME} RabbitMQ connection closed`);
      this.connection = undefined;
      this.channel = undefined;
    });
    this.connection = connection;
    return connection;
  }

  private async getChannel(): Promise<Channel> {
    if (this.channel) {
      return this.channel;
    }
    const connection = await this.getConnection();
    const channel = await connection.createChannel();
    await channel.prefetch(10);
    this.channel = channel;
    return channel;
  }

  public async connect(): Promise<void> {
    await this.getChannel();
    console.log(`${SERVICE_NAME} RabbitMQ connected`);
  }

  public async publish(queue: string, message: unknown): Promise<void> {
    const channel = await this.getChannel();
    await channel.assertQueue(queue, { durable: true });
    channel.sendToQueue(queue, Buffer.from(JSON.stringify(message)), { persistent: true });
  }

  public async consume<T = unknown>(queue: string, handler: Handler<T>): Promise<void> {
    const channel = await this.getChannel();
    await channel.assertQueue(queue, { durable: true });
    await channel.consume(queue, async (msg: ConsumeMessage | null) => {
      if (!msg) {
        return;
      }
      try {
        const payload = JSON.parse(msg.content.toString()) as T;
        await handler(payload);
        channel.ack(msg);
      } catch (err) {
        console.error(`${SERVICE_NAME} Failed to process ${queue} message`, err);
        channel.nack(msg, false, false);
      }
    });
  }
}

import '../ioc';
import { container } from 'tsyringe';
import { MessageBroker } from './message-broker';
import { IProfileService, PROFILE_SERVICE } from '../services/profile';

export const QUEUES = {
  USER_REGISTERED: 'user.registered',
  EMPLOYER_PROFILE_CREATED: 'employer.profile.created',
  RESUME_UPDATED: 'resume.updated',
} as const;

export const broker = new MessageBroker();

interface UserRegisteredPayload {
  id: string;
  email?: string;
}

export async function initMessaging(): Promise<void> {
  await broker.connect();
  await broker.consume<UserRegisteredPayload>(QUEUES.USER_REGISTERED, async (payload) => {
    if (!payload?.id) {
      console.warn('[profiles-service] user.registered payload without id');
      return;
    }
    try {
      const profileService = container.resolve<IProfileService>(PROFILE_SERVICE);
      const existing = await profileService.getMy(payload.id);
      if (existing) {
        return;
      }
      const fallbackName = payload.email ?? 'New user';
      await profileService.createForUser(payload.id, { fullName: fallbackName });
      console.log(`[profiles-service] Auto-created profile for ${payload.id}`);
    } catch (err) {
      console.error('[profiles-service] Failed to create profile from user.registered', err);
    }
  });
}

import dataSource from '../config/data-source';
import { Repository } from 'typeorm';
import { JobSeekerProfile } from '../models/JobSeekerProfile';
import { singleton } from 'tsyringe';

export interface ProfileCreateDto { fullName: string; }
export interface ProfileUpdateDto { fullName?: string; }

export interface IProfileService {
  getMy(userId: string): Promise<JobSeekerProfile | null>;
  createForUser(userId: string, dto: ProfileCreateDto): Promise<JobSeekerProfile>;
  updateMy(userId: string, dto: ProfileUpdateDto): Promise<JobSeekerProfile>;
}

export const PROFILE_SERVICE = Symbol('IProfileService');

@singleton()
export class ProfileService implements IProfileService {
  private repo: Repository<JobSeekerProfile> = dataSource.getRepository(JobSeekerProfile);

  async getMy(userId: string): Promise<JobSeekerProfile | null> {
    return this.repo.findOne({ where: { userId } });
  }

  async createForUser(userId: string, dto: ProfileCreateDto): Promise<JobSeekerProfile> {
    const existing = await this.getMy(userId);
    if (existing) { throw new Error('Profile already exists'); }
    const entity = this.repo.create({ userId, fullName: dto.fullName });
    return this.repo.save(entity);
  }

  async updateMy(userId: string, dto: ProfileUpdateDto): Promise<JobSeekerProfile> {
    const p = await this.getMy(userId);
    if (!p) { throw new Error('Profile not found'); }
    if (dto.fullName !== undefined) p.fullName = dto.fullName;
    return this.repo.save(p);
  }
}

