    return p;
  }

  @Post()
  @SuccessResponse('201')
  @Response<Error>(400, 'Bad Request')
  public async create(@Body() dto: EmployerCreateDto, @Request() req: any): Promise<EmployerProfile> {
    const user = req?.user as { id: string } | undefined; if (!user?.id) { this.setStatus(401); throw new Error('Unauthorized'); }
    const p = await this.service.createForUser(user.id, dto);
    this.setStatus(201);
    return p;
  }
}

import amqp, { Channel, ChannelModel, ConsumeMessage } from 'amqplib';
import SETTINGS from '../config/settings';

type Handler<T> = (payload: T) => Promise<void>;

const SERVICE_NAME = '[vacancies-service]';

export class MessageBroker {
  private connection?: ChannelModel;
  private channel?: Channel;

  private async getConnection(): Promise<ChannelModel> {
    if (this.connection) {
      return this.connection;
    }
    const connection = await amqp.connect(SETTINGS.MESSAGE_BROKER_URL);
    connection.on('error', (err: Error) => {
      console.error(`${SERVICE_NAME} RabbitMQ connection error`, err);
    });
    connection.on('close', () => {
      console.warn(`${SERVICE_NAME} RabbitMQ connection closed`);
      this.connection = undefined;
      this.channel = undefined;
    });
    this.connection = connection;
    return connection;
  }

  private async getChannel(): Promise<Channel> {
    if (this.channel) {
      return this.channel;
    }
    const connection = await this.getConnection();
    const channel = await connection.createChannel();
    await channel.prefetch(10);
    this.channel = channel;
    return channel;
  }

  public async connect(): Promise<void> {
    await this.getChannel();
    console.log(`${SERVICE_NAME} RabbitMQ connected`);
  }

  public async publish(queue: string, message: unknown): Promise<void> {
    const channel = await this.getChannel();
    await channel.assertQueue(queue, { durable: true });
    channel.sendToQueue(queue, Buffer.from(JSON.stringify(message)), { persistent: true });
  }

  public async consume<T = unknown>(queue: string, handler: Handler<T>): Promise<void> {
    const channel = await this.getChannel();
    await channel.assertQueue(queue, { durable: true });
    await channel.consume(queue, async (msg: ConsumeMessage | null) => {
      if (!msg) {
        return;
      }
      try {
        const payload = JSON.parse(msg.content.toString()) as T;
        await handler(payload);
        channel.ack(msg);
      } catch (err) {
        console.error(`${SERVICE_NAME} Failed to process ${queue} message`, err);
        channel.nack(msg, false, false);
      }
    });
  }
}

import { MessageBroker } from './message-broker';

export const QUEUES = {
  USER_REGISTERED: 'user.registered',
  EMPLOYER_PROFILE_CREATED: 'employer.profile.created',
  RESUME_UPDATED: 'resume.updated',
} as const;

export const broker = new MessageBroker();

interface ResumeUpdatedPayload {
  userId: string;
  resumeId: string;
  action: string;
  [key: string]: unknown;
}

export async function initMessaging(): Promise<void> {
  await broker.connect();
  await broker.consume<ResumeUpdatedPayload>(QUEUES.RESUME_UPDATED, async (payload) => {
    if (!payload?.userId || !payload?.resumeId) {
      console.warn('[vacancies-service] resume.updated payload missing userId or resumeId');
      return;
    }
    console.log('[vacancies-service] resume.updated received', payload);
  });
}

import dataSource from '../config/data-source';
import { Repository } from 'typeorm';
import { EmployerProfile } from '../models/EmployerProfile';
import { Company } from '../models/Company';
import { singleton } from 'tsyringe';
import { broker, QUEUES } from '../messaging';

export interface EmployerCreateDto { companyId: string; phone: string }

export interface IEmployerService {
  getMy(userId: string): Promise<EmployerProfile | null>;
  createForUser(userId: string, dto: EmployerCreateDto): Promise<EmployerProfile>;
}

export const EMPLOYER_SERVICE = Symbol('IEmployerService');

@singleton()
export class EmployerService implements IEmployerService {
  private repo: Repository<EmployerProfile> = dataSource.getRepository(EmployerProfile);
  private companyRepo: Repository<Company> = dataSource.getRepository(Company);

  async getMy(userId: string): Promise<EmployerProfile | null> {
    return this.repo.createQueryBuilder('e')
      .leftJoinAndSelect('e.company', 'c')
      .where('e.userId = :uid', { uid: userId })
      .getOne();
  }

  async createForUser(userId: string, dto: EmployerCreateDto): Promise<EmployerProfile> {
    const company = await this.companyRepo.findOne({ where: { id: dto.companyId } });
    if (!company) { throw new Error('Company not found'); }
    const entity = this.repo.create({ company, phone: dto.phone, userId });
    const saved = await this.repo.save(entity);
    try {
      await broker.publish(QUEUES.EMPLOYER_PROFILE_CREATED, {
        userId,
        companyId: company.id,
        phone: dto.phone,
      });
      console.log(`[vacancies-service] Published employer.profile.created for ${userId}`);
    } catch (err) {
      console.error('[vacancies-service] Failed to publish employer.profile.created event', err);
    }
    return saved;
  }
}

import { Repository } from 'typeorm';
import dataSource from '../config/data-source';
import { Vacancy } from '../models/Vacancy';
import { Industry } from '../common/enums';
import { EmployerProfile } from '../models/EmployerProfile';
import { singleton } from 'tsyringe';

export interface VacancySearchParams { industry?: Industry; salaryMin?: number; salaryMax?: number; experienceMin?: number; experienceMax?: number; }
export interface VacancyCreateDto { title: string; description: string; requirements?: string; salaryMin?: number; salaryMax?: number; industry: Industry; experienceRequired?: number; expireDate?: Date; }
export interface VacancyUpdateDto extends Partial<VacancyCreateDto> {}

export interface IVacancyService {
  search(params: VacancySearchParams): Promise<Vacancy[]>;
  getById(id: string): Promise<Vacancy>;
  createForEmployer(userId: string, dto: VacancyCreateDto): Promise<Vacancy>;
  updateForEmployer(userId: string, id: string, dto: VacancyUpdateDto): Promise<Vacancy>;
  deleteForEmployer(userId: string, id: string): Promise<void>;
}

export const VACANCY_SERVICE = Symbol('IVacancyService');

@singleton()
export class VacancyService implements IVacancyService {
  private readonly repo: Repository<Vacancy> = dataSource.getRepository(Vacancy);
  private readonly employerRepo: Repository<EmployerProfile> = dataSource.getRepository(EmployerProfile);

  public async search(params: VacancySearchParams): Promise<Vacancy[]> {
    const qb = this.repo.createQueryBuilder('v').leftJoinAndSelect('v.company', 'company');
    if (params.industry) qb.andWhere('v.industry = :industry', { industry: params.industry });
    if (typeof params.salaryMin === 'number') qb.andWhere('(v.salaryMax IS NULL OR v.salaryMax >= :salaryMin)', { salaryMin: params.salaryMin });
    if (typeof params.salaryMax === 'number') qb.andWhere('(v.salaryMin IS NULL OR v.salaryMin <= :salaryMax)', { salaryMax: params.salaryMax });
    if (typeof params.experienceMin === 'number') qb.andWhere('(v.experienceRequired IS NULL OR v.experienceRequired >= :expMin)', { expMin: params.experienceMin });
    if (typeof params.experienceMax === 'number') qb.andWhere('(v.experienceRequired IS NULL OR v.experienceRequired <= :expMax)', { expMax: params.experienceMax });
    qb.orderBy('v.postedDate', 'DESC');
    return qb.getMany();
  }

  public async getById(id: string): Promise<Vacancy> {
    const v = await this.repo.findOne({ where: { id }, relations: { company: true } });
    if (!v) throw new Error(`Vacancy(${id}) not found`); return v;
  }

  private async getEmployerProfileByUserIdOrThrow(userId: string): Promise<EmployerProfile> {
    const ep = await this.employerRepo.findOne({
      where: { userId },
      relations: { company: true }, // важно
    });
    if (!ep) throw new Error('Employer profile not found for current user');
    return ep;
  }

  public async createForEmployer(userId: string, dto: VacancyCreateDto): Promise<Vacancy> {
    const ep = await this.getEmployerProfileByUserIdOrThrow(userId);
    const entity = this.repo.create({ company: ep.company, employerProfile: ep, title: dto.title, description: dto.description, requirements: dto.requirements, salaryMin: dto.salaryMin, salaryMax: dto.salaryMax, industry: dto.industry, experienceRequired: dto.experienceRequired, postedDate: new Date(), expireDate: dto.expireDate });
    return this.repo.save(entity);
  }

  public async updateForEmployer(userId: string, id: string, dto: VacancyUpdateDto): Promise<Vacancy> {
    await this.getEmployerProfileByUserIdOrThrow(userId);
    const v = await this.repo.findOne({ where: { id } }); if (!v) throw new Error(`Vacancy(${id}) not found`);
    Object.assign(v, dto); return this.repo.save(v);
  }

  public async deleteForEmployer(userId: string, id: string): Promise<void> {
    await this.getEmployerProfileByUserIdOrThrow(userId);
    const v = await this.repo.findOne({ where: { id } }); if (!v) throw new Error(`Vacancy(${id}) not found`);
    await this.repo.remove(v);
  }
}

import dataSource from '../config/data-source';
import { Repository } from 'typeorm';
import { Company } from '../models/Company';
import { Industry } from '../common/enums';
import { singleton } from 'tsyringe';

export interface CompanyCreateDto {
  name: string;
  description?: string;
  industry: Industry;
  website?: string;
  address?: string;
  phone?: string;
  email?: string;
  foundedDate?: string;
  employeesCount?: number;
}

export type CompanyUpdateDto = Partial<CompanyCreateDto>;

export interface ICompanyService {
  list(): Promise<Company[]>;
  getById(id: string): Promise<Company>;
  create(dto: CompanyCreateDto): Promise<Company>;
  update(id: string, dto: CompanyUpdateDto): Promise<Company>;
  remove(id: string): Promise<void>;
}

export const COMPANY_SERVICE = Symbol('ICompanyService');

@singleton()
export class CompanyService implements ICompanyService {
  private repo: Repository<Company> = dataSource.getRepository(Company);

  async list(): Promise<Company[]> { return this.repo.find(); }
  async getById(id: string): Promise<Company> {
    const c = await this.repo.findOne({ where: { id } });
    if (!c) throw new Error('Company not found');
    return c;
  }
  async create(dto: CompanyCreateDto): Promise<Company> {
    const entity: Company = this.repo.create(dto as Company);
return this.repo.save(entity);
  }
  async update(id: string, dto: CompanyUpdateDto): Promise<Company> {
    const c = await this.getById(id);
    Object.assign(c, dto);
    return this.repo.save(c);
  }
  async remove(id: string): Promise<void> { await this.repo.delete(id); }
}

