import type { IocContainer, ServiceIdentifier } from '@tsoa/runtime';
import { AuthService } from './services/auth';
import { AUTH_SERVICE } from './services/auth.types';

export const iocContainer: IocContainer = {
  get<T>(controller: ServiceIdentifier<T>): T | Promise<T> {
    return container.resolve(controller as any);
  },
};

// Register interface-based bindings
container.register(AUTH_SERVICE, { useClass: AuthService });

import { env } from 'process';

class Settings {
  APP_ENVIRONMENT: string = env.NODE_ENV || 'development';
  APP_HOST: string = env.APP_HOST || '0.0.0.0';
  APP_PORT: number = env.APP_PORT ? parseInt(env.APP_PORT) : 8001;
  APP_PROTOCOL: string = env.APP_PROTOCOL || 'http';
  APP_API_PREFIX: string = '/api/auth';

  DB_HOST = env.DB_HOST || 'localhost';
  DB_PORT = env.DB_PORT ? parseInt(env.DB_PORT) : 5432;
  DB_NAME = env.DB_NAME || 'postgres';
  DB_USER = env.DB_USER || 'postgres';
  DB_PASSWORD = env.DB_PASSWORD || 'postgres';
  DB_SCHEMA = env.DB_SCHEMA || 'auth';
  private isProduction = (env.NODE_ENV === 'production');
  DB_ENTITIES = this.isProduction ? 'dist/models/**/*.js' : 'src/models/**/*.ts';
  DB_MIGRATIONS = this.isProduction ? 'dist/migrations/**/*.js' : 'src/migrations/**/*.ts';
  JWT_SECRET_KEY = env.JWT_SECRET_KEY || 'secret';
  MESSAGE_BROKER_URL: string = env.MESSAGE_BROKER_URL || 'amqp://localhost:5672';
}

export default new Settings();

import 'reflect-metadata';
import { DataSource } from 'typeorm';
import SETTINGS from './settings';

const dataSource = new DataSource({
  type: 'postgres',
  host: SETTINGS.DB_HOST,
  port: SETTINGS.DB_PORT,
  username: SETTINGS.DB_USER,
  password: SETTINGS.DB_PASSWORD,
  database: SETTINGS.DB_NAME,
  logging: true,
  synchronize: false,
  schema: SETTINGS.DB_SCHEMA,
  entities: [SETTINGS.DB_ENTITIES],
  migrations: [SETTINGS.DB_MIGRATIONS],
});

export default dataSource;

import { Request } from 'express';
import { Unauthorized } from 'http-errors';
import jwt, { JwtPayload } from 'jsonwebtoken';
import SETTINGS from '../config/settings';

export async function expressAuthentication(
  request: Request,
  securityName: string,
  _scopes?: string[]
): Promise<JwtPayload> {
  if (securityName !== 'bearerAuth') {
    throw new Error(`Unknown security scheme: ${securityName}`);
  }
  const auth = request.headers.authorization;
  if (!auth || !auth.startsWith('Bearer ')) {
    throw new Unauthorized('No Bearer token provided');
  }
  const token = auth.slice(7);
  try {
    const payload = jwt.verify(token, SETTINGS.JWT_SECRET_KEY) as JwtPayload;
    (request as any).user = payload;
    return payload;
  } catch {
    throw new Unauthorized('Invalid or expired token');
  }
}

export enum UserRole {
  Employer = 'employer',
  JobSeeker = 'jobseeker',
  Admin = 'admin'
}

import { Entity, Column, PrimaryGeneratedColumn, CreateDateColumn, UpdateDateColumn } from 'typeorm';
import { UserRole } from './enums';

@Entity({ name: 'users' })
export class User {
  @PrimaryGeneratedColumn('uuid')
  id!: string;

  @CreateDateColumn({ name: 'created_at' })
  createdAt!: Date;

  @UpdateDateColumn({ name: 'updated_at' })
  updatedAt!: Date;

  @Column({ unique: true })
  email!: string;

  @Column()
  passwordHash!: string;

  @Column({ type: 'enum', enum: UserRole, enumName: 'user_role', default: UserRole.JobSeeker })
  role!: UserRole;
}

import { Body, Controller, Post, Response, Route, SuccessResponse, Tags } from 'tsoa';
import { IAuthService, AUTH_SERVICE } from '../services/auth.types';
import { inject, injectable } from 'tsyringe';
import { User } from '../models/User';

interface RegisterRequest { email: string; password: string; }
interface LoginRequest { email: string; password: string; }
interface AuthResponse { accessToken: string; }

@Route('')
@Tags('Auth')
@injectable()
export class AuthController extends Controller {
  constructor(@inject(AUTH_SERVICE) private service: IAuthService) { super(); }

  @Post('register')
  @SuccessResponse('201', 'Created')
  @Response<Error>(400, 'Bad Request')
  public async register(@Body() body: RegisterRequest): Promise<User> {
    const user = await this.service.register(body.email, body.password);
    this.setStatus(201);
    return user;
  }

  @Post('login')
  @SuccessResponse('200', 'OK')
  @Response<Error>(401, 'Unauthorized')
  public async login(@Body() body: LoginRequest): Promise<AuthResponse> {
    return this.service.login(body.email, body.password);
  }
}

import amqp, { Channel, ChannelModel, ConsumeMessage } from 'amqplib';
import SETTINGS from '../config/settings';

type Handler<T> = (payload: T) => Promise<void>;

const SERVICE_NAME = '[auth-service]';

export class MessageBroker {
  private connection?: ChannelModel;
  private channel?: Channel;

  private async getConnection(): Promise<ChannelModel> {
    if (this.connection) {
      return this.connection;
    }
    const connection = await amqp.connect(SETTINGS.MESSAGE_BROKER_URL);
    connection.on('error', (err: Error) => {
      console.error(`${SERVICE_NAME} RabbitMQ connection error`, err);
    });
    connection.on('close', () => {
      console.warn(`${SERVICE_NAME} RabbitMQ connection closed`);
      this.connection = undefined;
      this.channel = undefined;
    });
    this.connection = connection;
    return connection;
  }

  private async getChannel(): Promise<Channel> {
    if (this.channel) {
      return this.channel;
    }
    const connection = await this.getConnection();
    const channel = await connection.createChannel();
    await channel.prefetch(10);
    this.channel = channel;
    return channel;
  }

  public async connect(): Promise<void> {
    await this.getChannel();
    console.log(`${SERVICE_NAME} RabbitMQ connected`);
  }

  public async publish(queue: string, message: unknown): Promise<void> {
    const channel = await this.getChannel();
    await channel.assertQueue(queue, { durable: true });
    channel.sendToQueue(queue, Buffer.from(JSON.stringify(message)), { persistent: true });
  }

  public async consume<T = unknown>(queue: string, handler: Handler<T>): Promise<void> {
    const channel = await this.getChannel();
    await channel.assertQueue(queue, { durable: true });
    await channel.consume(queue, async (msg: ConsumeMessage | null) => {
      if (!msg) {
        return;
      }
      try {
        const payload = JSON.parse(msg.content.toString()) as T;
        await handler(payload);
        channel.ack(msg);
      } catch (err) {
        console.error(`${SERVICE_NAME} Failed to process ${queue} message`, err);
        channel.nack(msg, false, false);
      }
    });
  }
}

import dataSource from '../config/data-source';
import { User } from '../models/User';
import { UserRole } from '../models/enums';
import { MessageBroker } from './message-broker';

export const QUEUES = {
  USER_REGISTERED: 'user.registered',
  EMPLOYER_PROFILE_CREATED: 'employer.profile.created',
  RESUME_UPDATED: 'resume.updated',
} as const;

export const broker = new MessageBroker();

interface EmployerProfileCreatedPayload {
  userId: string;
  companyId?: string;
  phone?: string;
}

export async function initMessaging(): Promise<void> {
  await broker.connect();
  await broker.consume<EmployerProfileCreatedPayload>(QUEUES.EMPLOYER_PROFILE_CREATED, async (payload) => {
    if (!payload?.userId) {
      console.warn('[auth-service] employer.profile.created payload without userId');
      return;
    }
    try {
      const repo = dataSource.getRepository(User);
      await repo.update({ id: payload.userId }, { role: UserRole.Employer });
      console.log(`[auth-service] Role updated to employer for user ${payload.userId}`);
    } catch (err) {
      console.error('[auth-service] Failed to update user role from employer event', err);
    }
  });
}

import { User } from '../models/User';

export interface IAuthService {
  register(email: string, password: string): Promise<User>;
  login(email: string, password: string): Promise<{ accessToken: string }>;
}

// Injection token for DI container
export const AUTH_SERVICE = Symbol('IAuthService');

import jwt from 'jsonwebtoken';
import { Repository } from 'typeorm';
import SETTINGS from '../config/settings';
import dataSource from '../config/data-source';
import { User } from '../models/User';
import bcrypt from 'bcryptjs';
import { singleton } from 'tsyringe';
import { IAuthService } from './auth.types';
import { broker, QUEUES } from '../messaging';

@singleton()
export class AuthService implements IAuthService {
  private readonly userRepo: Repository<User>;
  constructor() {
    this.userRepo = dataSource.getRepository(User);
  }

  public async register(email: string, password: string): Promise<User> {
    const existing = await this.userRepo.findOne({ where: { email } });
    if (existing) throw new Error('Email already in use');
    const hash = await bcrypt.hash(password, 10);
    const user = this.userRepo.create({ email, passwordHash: hash });
    const saved = await this.userRepo.save(user);
    try {
      await broker.publish(QUEUES.USER_REGISTERED, { id: saved.id, email: saved.email });
      console.log(`[auth-service] Published user.registered for ${saved.email}`);
    } catch (err) {
      console.error('[auth-service] Failed to publish user.registered event', err);
    }
    return saved;
  }

  public async login(email: string, password: string): Promise<{ accessToken: string }> {
    const user = await this.userRepo.findOne({ where: { email } });
    if (!user) throw new Error('Invalid credentials');
    const ok = await bcrypt.compare(password, user.passwordHash);
    if (!ok) throw new Error('Invalid credentials');
    const token = jwt.sign({ id: user.id, email: user.email }, SETTINGS.JWT_SECRET_KEY, { expiresIn: 60 * 5 });
    return { accessToken: token };
  }
}

events {}
http {
  server {
    listen 80;
    client_max_body_size 10m;

    rewrite ^/api/(auth|vacancies|profiles)/docs$ /api/$1/docs/ permanent;

    # shortcut redirects removed; use explicit /api/<service> paths

    location /api/auth/ {
      proxy_pass http://auth_service:8001/;
      proxy_set_header Host $host;
      proxy_set_header X-Real-IP $remote_addr;
      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    }

    location /api/vacancies/ {
      proxy_pass http://vacancies_service:8002/;
      proxy_set_header Host $host;
      proxy_set_header X-Real-IP $remote_addr;
      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    }

    location /api/profiles/ {
      proxy_pass http://profiles_service:8003/;
      proxy_set_header Host $host;
      proxy_set_header X-Real-IP $remote_addr;
      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
    }
  }
}
